      console.log(obj)

      obj.data.x = Xc + Math.round(radius * Math.cos(angle));
      obj.data.y = offSetY;
      obj.data.z = Zc + Math.round(radius * Math.sin(angle));
      obj.data.rotateY = offsetRotation;
      obj.data.rotateX = 0;
      obj.data.rotateZ = 0;

